package week1.day1;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int mark = -0;
		
		if (mark>35)
		{
			System.out.println("Mark Is : Pass ");
		}
		
		else if (mark<35)
		{
			System.out.println("Mark Is : Fail");
		}
		else
		{
			System.out.println("Mark is : Absent");
		}

	}

}
